interface CourseData {
  code: string;
  name: string;
  credits: number;
  category: 'core' | 'major' | 'elective' | 'general' |'free';
  mainCategory?:
    | 'หมวดวิชาศึกษาทั่วไป'
    | 'หมวดวิชาเฉพาะ'
    | 'หมวดวิชาเลือกเสรี';
  subCategory?:
    | 'กลุ่มวิชาภาษา'
    | 'กลุ่มวิชาวิทยาศาสตร์และคณิตศาสตร์'
    | 'กลุ่มวิชาสังคมศาสตร์และมนุษยศาสตร์'
    | 'กลุ่มวิชากีฬาและนันทนาการ'
    | 'กลุ่มวิชาบูรณาการ'
    | 'กลุ่มวิชาเลือกในหมวดศึกษาทั่วไป'
    | 'กลุ่มวิชาแกน'
    | 'กลุ่มวิชาชีพ'
    | 'กลุ่มวิชาฝึกงาน/สหกิจศึกษา';
  prerequisites?: string[];
  corequisites?: string[];
  description?: string;
}

interface SemesterData {
  [key: string]: CourseData[];
}

interface CurriculumData {
  [year: string]: SemesterData;
}

interface ProgramData {
  [program: string]: CurriculumData;
}

// Forward declaration
let courseDatabase: ProgramData;

// Function to generate courses for a specific semester
export const generateCoursesForSemester = (
  programCode: string, 
  curriculumYear: string, 
  year: string, 
  semester: string, 
  courseCount: number = 6
) => {
  const semesterKey = `${year}-${semester}`;
  const programData = courseDatabase?.[programCode];
  
  if (programData && programData[curriculumYear]) {
    const curriculumData = programData[curriculumYear];
    const semesterCourses = curriculumData[semesterKey];
    
    if (semesterCourses && Array.isArray(semesterCourses)) {
      return semesterCourses.map((course) => {
        const baseDescription = course.description ?? '';
        const mainCategory = course.mainCategory ?? '';
        const subCategory = course.subCategory ?? '';
        const finalDescription = baseDescription;

        return {
          id: `${course.code}-${curriculumYear}`,
          code: course.code,
          name: course.name,
          credits: course.credits,
          description: finalDescription,
          prerequisites: course.prerequisites || [],
          corequisites: course.corequisites || [],
          category: course.category,
          mainCategory,
          subCategory,
          semester: parseInt(semester),
          year: parseInt(year),
          isActive: true
        };
      });
    }
  }
  
  // Fallback to generated courses if specific data not found
  const courses = [];
  const yearNum = parseInt(year);
  const semesterNum = parseInt(semester);
  for (let i = 1; i <= courseCount; i++) {
    const courseNumber = (yearNum - 1) * 200 + semesterNum * 100 + i;
    courses.push({
      id: `${programCode}${courseNumber}-${curriculumYear}`,
      code: `${programCode}${courseNumber}`,
      name: `Course ${i} - Year ${year} Semester ${semester}`,
      credits: Math.floor(Math.random() * 3) + 1,
      description: '',
      prerequisites: [],
      corequisites: [],
      category: (i <= 3 ? 'core' : (i <= 5 ? 'major' : 'elective')) as 'core' | 'major' | 'elective' | 'general',
      mainCategory: '',
      subCategory: '',
      semester: semesterNum,
      year: yearNum,
      isActive: true
    });
  }
  return courses;
};

