const updateINECoopData = () => {
  // อัปเดตข้อมูล INE-COOP จาก INE
  if (courseDatabase['INE'] && courseDatabase['INE-COOP']) {
    if (courseDatabase['INE']['62 สหกิจ']) {
      // คัดลอกข้อมูลทั้งหมดจาก INE 62 สหกิจ
      courseDatabase['INE-COOP']['62'] = JSON.parse(JSON.stringify(courseDatabase['INE']['62 สหกิจ']));
    }
    if (courseDatabase['INE']['67 สหกิจ']) {
      // คัดลอกข้อมูลทั้งหมดจาก INE 67 สหกิจ
      courseDatabase['INE-COOP']['67'] = JSON.parse(JSON.stringify(courseDatabase['INE']['67 สหกิจ']));
    }
  }
};

