setTimeout(() => {
  updateINECoopData();
}, 0);

// Function to get all courses from the database
export const getAllCourses = () => {
  const allCourses = [];
  
  if (courseDatabase) {
    for (const programCode in courseDatabase) {
      const programData = courseDatabase[programCode];
      for (const curriculumYear in programData) {
        const curriculumData = programData[curriculumYear];
        for (const semesterKey in curriculumData) {
          const semesterCourses = curriculumData[semesterKey];
          if (Array.isArray(semesterCourses)) {
            semesterCourses.forEach(course => {
              allCourses.push({
                id: `${course.code}-${curriculumYear}`,
                code: course.code,
                name: course.name,
                credits: course.credits,
                description: course.description || '',
                prerequisites: course.prerequisites || [],
                corequisites: course.corequisites || [],
                category: course.category,
                mainCategory: course.mainCategory || '',
                subCategory: course.subCategory || '',
                programCode,
                curriculumYear,
                isActive: true
              });
            });
          }
        }
      }
    }
  }
  
  return allCourses;
};

